import{e as t}from"./app.0186264c.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const d={},c=t('<h1 id="\u914D\u7F6E-watch" tabindex="-1"><a class="header-anchor" href="#\u914D\u7F6E-watch" aria-hidden="true">#</a> \u914D\u7F6E Watch</h1><p>\u7F16\u8BD1\u5668\u652F\u6301\u4F7F\u7528\u73AF\u5883\u53D8\u91CF\u914D\u7F6E\u5982\u4F55\u76D1\u89C6\u6587\u4EF6\u548C\u76EE\u5F55\u7684\u53D8\u5316\u3002</p><h2 id="\u4F7F\u7528tsc-watchfile\u73AF\u5883\u53D8\u91CF\u6765\u914D\u7F6E\u6587\u4EF6\u76D1\u89C6" tabindex="-1"><a class="header-anchor" href="#\u4F7F\u7528tsc-watchfile\u73AF\u5883\u53D8\u91CF\u6765\u914D\u7F6E\u6587\u4EF6\u76D1\u89C6" aria-hidden="true">#</a> \u4F7F\u7528<code>TSC_WATCHFILE</code>\u73AF\u5883\u53D8\u91CF\u6765\u914D\u7F6E\u6587\u4EF6\u76D1\u89C6</h2><table><thead><tr><th style="text-align:left;">\u9009\u9879</th><th style="text-align:left;">\u63CF\u8FF0</th></tr></thead><tbody><tr><td style="text-align:left;"><code>PriorityPollingInterval</code></td><td style="text-align:left;">\u4F7F\u7528<code>fs.watchFile</code>\u4F46\u9488\u5BF9\u6E90\u7801\u6587\u4EF6\uFF0C\u914D\u7F6E\u6587\u4EF6\u548C\u6D88\u5931\u7684\u6587\u4EF6\u4F7F\u7528\u4E0D\u540C\u7684\u8F6E\u8BE2\u95F4\u9694</td></tr><tr><td style="text-align:left;"><code>DynamicPriorityPolling</code></td><td style="text-align:left;">\u4F7F\u7528\u52A8\u6001\u961F\u5217\uFF0C\u5BF9\u7ECF\u5E38\u88AB\u4FEE\u6539\u7684\u6587\u4EF6\u4F7F\u7528\u8F83\u77ED\u7684\u8F6E\u8BE2\u95F4\u9694\uFF0C\u5BF9\u672A\u4FEE\u6539\u7684\u6587\u4EF6\u4F7F\u7528\u8F83\u957F\u7684\u8F6E\u8BE2\u95F4\u9694</td></tr><tr><td style="text-align:left;"><code>UseFsEvents</code></td><td style="text-align:left;">\u4F7F\u7528 <code>fs.watch</code>\uFF0C\u5B83\u4F7F\u7528\u6587\u4EF6\u7CFB\u7EDF\u4E8B\u4EF6\uFF08\u4F46\u5728\u4E0D\u540C\u7684\u7CFB\u7EDF\u4E0A\u53EF\u80FD\u4E0D\u4E00\u5B9A\u51C6\u786E\uFF09\u6765\u67E5\u8BE2\u6587\u4EF6\u7684\u4FEE\u6539/\u521B\u5EFA/\u5220\u9664\u3002\u6CE8\u610F\u5C11\u6570\u7684\u7CFB\u7EDF\u5982Linux\uFF0C\u5BF9\u76D1\u89C6\u8005\u7684\u6570\u91CF\u6709\u9650\u5236\uFF0C\u5982\u679C\u4F7F\u7528<code>fs.watch</code>\u521B\u5EFA\u76D1\u89C6\u5931\u8D25\u90A3\u4E48\u5C06\u901A\u8FC7<code>fs.watchFile</code>\u6765\u521B\u5EFA\u76D1\u89C6</td></tr><tr><td style="text-align:left;"><code>UseFsEventsWithFallbackDynamicPolling</code></td><td style="text-align:left;">\u6B64\u9009\u9879\u4E0E<code>UseFsEvents</code>\u7C7B\u4F3C\uFF0C\u53EA\u4E0D\u8FC7\u5F53\u4F7F\u7528<code>fs.watch</code>\u521B\u5EFA\u76D1\u89C6\u5931\u8D25\u540E\uFF0C\u56DE\u9000\u5230\u4F7F\u7528\u52A8\u6001\u8F6E\u8BE2\u961F\u5217\u8FDB\u884C\u76D1\u89C6\uFF08\u5982<code>DynamicPriorityPolling</code>\u4ECB\u7ECD\u7684\u90A3\u6837\uFF09</td></tr><tr><td style="text-align:left;"><code>UseFsEventsOnParentDirectory</code></td><td style="text-align:left;">\u6B64\u9009\u9879\u901A\u8FC7<code>fs.watch</code>\uFF08\u4F7F\u7528\u7CFB\u7EDF\u6587\u4EF6\u4E8B\u4EF6\uFF09\u76D1\u89C6\u6587\u4EF6\u7684\u7236\u76EE\u5F55\uFF0C\u56E0\u6B64CPU\u5360\u7528\u7387\u4F4E\u4F46\u4E5F\u4F1A\u964D\u4F4E\u7CBE\u5EA6</td></tr><tr><td style="text-align:left;">\u9ED8\u8BA4 \uFF08\u65E0\u6307\u5B9A\u503C\uFF09</td><td style="text-align:left;">\u5982\u679C\u73AF\u5883\u53D8\u91CF<code>TSC_NONPOLLING_WATCHER</code>\u8BBE\u7F6E\u4E3A<code>true</code>\uFF0C\u76D1\u89C6\u6587\u4EF6\u7684\u7236\u76EE\u5F55\uFF08\u5982\u540C<code>UseFsEventsOnParentDirectory</code>\uFF09\u3002\u5426\u5219\uFF0C\u4F7F\u7528<code>fs.watchFile</code>\u76D1\u89C6\u6587\u4EF6\uFF0C\u8D85\u65F6\u65F6\u95F4\u4E3A<code>250ms</code>\u3002</td></tr></tbody></table><h2 id="\u4F7F\u7528tsc-watchdirectory\u73AF\u5883\u53D8\u91CF\u6765\u914D\u7F6E\u76EE\u5F55\u76D1\u89C6" tabindex="-1"><a class="header-anchor" href="#\u4F7F\u7528tsc-watchdirectory\u73AF\u5883\u53D8\u91CF\u6765\u914D\u7F6E\u76EE\u5F55\u76D1\u89C6" aria-hidden="true">#</a> \u4F7F\u7528<code>TSC_WATCHDIRECTORY</code>\u73AF\u5883\u53D8\u91CF\u6765\u914D\u7F6E\u76EE\u5F55\u76D1\u89C6</h2><p>\u5728\u90A3\u4E9BNodejs\u539F\u751F\u5C31\u4E0D\u652F\u6301\u9012\u5F52\u76D1\u89C6\u76EE\u5F55\u7684\u5E73\u53F0\u4E0A\uFF0C\u6211\u4EEC\u4F1A\u6839\u636E<code>TSC_WATCHDIRECTORY</code>\u7684\u4E0D\u540C\u9009\u9879\u9012\u5F52\u5730\u521B\u5EFA\u5BF9\u5B50\u76EE\u5F55\u7684\u76D1\u89C6\u3002 \u6CE8\u610F\u5728\u90A3\u4E9B\u539F\u751F\u5C31\u652F\u6301\u9012\u5F52\u76D1\u89C6\u76EE\u5F55\u7684\u5E73\u53F0\u4E0A\uFF08\u5982Windows\uFF09\uFF0C\u8FD9\u4E2A\u73AF\u5883\u53D8\u91CF\u4F1A\u88AB\u5FFD\u7565\u3002</p><table><thead><tr><th style="text-align:left;">\u9009\u9879</th><th style="text-align:left;">\u63CF\u8FF0</th></tr></thead><tbody><tr><td style="text-align:left;"><code>RecursiveDirectoryUsingFsWatchFile</code></td><td style="text-align:left;">\u4F7F\u7528<code>fs.watchFile</code>\u76D1\u89C6\u76EE\u5F55\u548C\u5B50\u76EE\u5F55\uFF0C\u5B83\u662F\u4E00\u4E2A\u8F6E\u8BE2\u76D1\u89C6\uFF08\u6D88\u8017CPU\u5468\u671F\uFF09</td></tr><tr><td style="text-align:left;"><code>RecursiveDirectoryUsingDynamicPriorityPolling</code></td><td style="text-align:left;">\u4F7F\u7528\u52A8\u6001\u8F6E\u8BE2\u961F\u5217\u6765\u83B7\u53D6\u76EE\u5F55\u4E0E\u5176\u5B50\u76EE\u5F55\u7684\u6539\u53D8</td></tr><tr><td style="text-align:left;">\u9ED8\u8BA4 \uFF08\u65E0\u6307\u5B9A\u503C\uFF09</td><td style="text-align:left;">\u4F7F\u7528<code>fs.watch</code>\u6765\u76D1\u89C6\u76EE\u5F55\u53CA\u5176\u5B50\u76EE\u5F55</td></tr></tbody></table><h2 id="\u80CC\u666F" tabindex="-1"><a class="header-anchor" href="#\u80CC\u666F" aria-hidden="true">#</a> \u80CC\u666F</h2><p>\u5728\u7F16\u8BD1\u5668\u4E2D<code>--watch</code>\u7684\u5B9E\u73B0\u4F9D\u8D56\u4E8ENodejs\u63D0\u4F9B\u7684<code>fs.watch</code>\u548C<code>fs.watchFile</code>\uFF0C\u4E24\u8005\u5404\u6709\u4F18\u7F3A\u70B9\u3002</p><p><code>fs.watch</code>\u4F7F\u7528\u6587\u4EF6\u7CFB\u7EDF\u4E8B\u4EF6\u901A\u77E5\u6587\u4EF6\u53CA\u76EE\u5F55\u7684\u53D8\u5316\u3002 \u4F46\u662F\u5B83\u4F9D\u8D56\u4E8E\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u4E14\u4E8B\u4EF6\u901A\u77E5\u5E76\u4E0D\u5B8C\u5168\u53EF\u9760\uFF0C\u5728\u5F88\u591A\u64CD\u4F5C\u7CFB\u7EDF\u4E0A\u7684\u884C\u4E3A\u96BE\u4EE5\u9884\u6599\u3002 \u8FD8\u53EF\u80FD\u4F1A\u6709\u521B\u5EFA\u76D1\u89C6\u4E2A\u6570\u7684\u9650\u5236\uFF0C\u5982Linux\u7CFB\u7EDF\uFF0C\u5728\u5305\u542B\u5927\u91CF\u6587\u4EF6\u7684\u7A0B\u5E8F\u4E2D\u76D1\u89C6\u5668\u4E2A\u6570\u5F88\u5FEB\u88AB\u8017\u5C3D\u3002 \u4F46\u4E5F\u6B63\u662F\u56E0\u4E3A\u5B83\u4F7F\u7528\u6587\u4EF6\u7CFB\u7EDF\u4E8B\u4EF6\uFF0C\u4E0D\u9700\u8981\u5360\u7528\u8FC7\u591A\u7684CPU\u5468\u671F\u3002 \u5178\u578B\u5730\uFF0C\u7F16\u8BD1\u5668\u4F7F\u7528<code>fs.watch</code>\u6765\u76D1\u89C6\u76EE\u5F55\uFF08\u6BD4\u5982\u914D\u7F6E\u6587\u4EF6\u91CC\u58F0\u660E\u7684\u6E90\u7801\u76EE\u5F55\uFF0C\u65E0\u6CD5\u8FDB\u884C\u6A21\u5757\u89E3\u6790\u7684\u76EE\u5F55\uFF09\u3002 \u8FD9\u6837\u5C31\u53EF\u4EE5\u5904\u7406\u6539\u52A8\u901A\u77E5\u4E0D\u51C6\u786E\u7684\u95EE\u9898\u3002 \u4F46\u9012\u5F52\u5730\u76D1\u89C6\u4EC5\u5728Windows\u548COSX\u7CFB\u7EDF\u4E0A\u652F\u6301\u3002 \u8FD9\u5C31\u610F\u5473\u7740\u5728\u5176\u5B83\u7CFB\u7EDF\u4E0A\u8981\u4F7F\u7528\u66FF\u4EE3\u65B9\u6848\u3002</p><p><code>fs.watchFile</code>\u4F7F\u7528\u8F6E\u8BE2\uFF0C\u56E0\u6B64\u6D89\u53CA\u5230CPU\u5468\u671F\u3002 \u4F46\u662F\u8FD9\u662F\u6700\u53EF\u9760\u7684\u83B7\u53D6\u6587\u4EF6/\u76EE\u5F55\u72B6\u6001\u7684\u673A\u5236\u3002 \u5178\u578B\u5730\uFF0C\u7F16\u8BD1\u5668\u4F7F\u7528<code>fs.watchFile</code>\u76D1\u89C6\u6E90\u6587\u4EF6\uFF0C\u914D\u7F6E\u6587\u4EF6\u548C\u6D88\u5931\u7684\u6587\u4EF6\uFF08\u5931\u53BB\u6587\u4EF6\u5F15\u7528\uFF09\uFF0C\u8FD9\u610F\u5473\u7740\u5BF9CPU\u7684\u4F7F\u7528\u4F9D\u8D56\u4E8E\u7A0B\u5E8F\u91CC\u6587\u4EF6\u7684\u6570\u91CF\u3002</p>',11);function a(o,l){return c}var s=e(d,[["render",a]]);export{s as default};
